#!/bin/bash

echo "๐จ Forcing Next.js to Compile Homepage..."
echo ""

cd "$(dirname "$0")"

# STOP NEXT.JS IF RUNNING
echo "๐ Stopping Next.js if running..."
pkill -f "next dev" 2>/dev/null || true
sleep 2

# CLEAR CACHE
echo "๐งน Clearing Next.js cache..."
rm -rf .next 2>/dev/null || true
rm -rf node_modules/.cache 2>/dev/null || true

echo "โ Cache cleared"
echo ""

# VERIFY FILES EXIST
echo "๐ Verifying required files..."

if [ ! -f "src/app/page.tsx" ]; then
    echo "โ src/app/page.tsx is missing!"
    exit 1
fi

if [ ! -f "src/app/layout.tsx" ]; then
    echo "โ src/app/layout.tsx is missing!"
    exit 1
fi

echo "โ Required files exist"
echo ""

# CHECK FOR SYNTAX ERRORS
echo "๐ Checking for syntax errors in page.tsx..."

if command -v node > /dev/null 2>&1; then
    # Try to parse the file with Node
    node -e "
        const fs = require('fs');
        const content = fs.readFileSync('src/app/page.tsx', 'utf8');
        // Check for basic syntax issues
        if (!content.includes('export default')) {
            console.error('โ No default export found');
            process.exit(1);
        }
        if (!content.includes('function Home') && !content.includes('const Home')) {
            console.error('โ Home component not found');
            process.exit(1);
        }
        console.log('โ Basic syntax check passed');
    " 2>&1
    
    if [ $? -ne 0 ]; then
        echo "โ Syntax check failed!"
        exit 1
    fi
fi

echo ""

# START NEXT.JS IN BACKGROUND AND CAPTURE OUTPUT
echo "๐ Starting Next.js with output capture..."
echo ""

# Create a log file
LOG_FILE="/tmp/nextjs-compile-$(date +%s).log"

# Start Next.js and capture output
npm run dev > "$LOG_FILE" 2>&1 &
NEXTJS_PID=$!

echo "โ Next.js started (PID: $NEXTJS_PID)"
echo "๐ Logs: $LOG_FILE"
echo ""

# WAIT FOR PORT
echo "โณ Waiting for port 3200..."
MAX_WAIT=30
WAITED=0

while [ $WAITED -lt $MAX_WAIT ]; do
    if ss -tuln 2>/dev/null | grep -q ":3200 " || netstat -tuln 2>/dev/null | grep -q ":3200 "; then
        echo "โ Port 3200 is listening"
        break
    fi
    sleep 1
    WAITED=$((WAITED + 1))
done

if [ $WAITED -ge $MAX_WAIT ]; then
    echo "โ Timeout waiting for port 3200"
    echo ""
    echo "๐ Last 50 lines of log:"
    tail -50 "$LOG_FILE"
    kill $NEXTJS_PID 2>/dev/null || true
    exit 1
fi

echo ""

# WAIT A BIT FOR INITIALIZATION
echo "โณ Waiting for Next.js to initialize..."
sleep 5

# TRIGGER COMPILATION
echo "๐ฅ Triggering homepage compilation..."
echo ""

for i in {1..10}; do
    HTTP_CODE=$(curl -s -o /dev/null -w "%{http_code}" http://localhost:3200/ 2>/dev/null || echo "000")
    
    echo "   Attempt $i/10 - HTTP $HTTP_CODE"
    
    if [ "$HTTP_CODE" = "200" ]; then
        echo ""
        echo "โ SUCCESS! Homepage compiled successfully!"
        echo "๐ Open: http://localhost:3200"
        echo ""
        echo "๐ Next.js is running in background (PID: $NEXTJS_PID)"
        echo "๐ View logs: tail -f $LOG_FILE"
        exit 0
    fi
    
    # Show recent log entries if we're getting errors
    if [ "$HTTP_CODE" != "000" ] && [ $i -eq 5 ]; then
        echo ""
        echo "๐ Recent compilation output:"
        tail -20 "$LOG_FILE" | grep -i "error\|warn\|fail" || tail -10 "$LOG_FILE"
        echo ""
    fi
    
    sleep 2
done

echo ""
echo "โ Still getting HTTP $HTTP_CODE after 10 attempts"
echo ""
echo "๐ Full compilation log (last 100 lines):"
